import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { VictoryChart, VictoryVoronoiContainer, VictoryGroup, VictoryTooltip, VictoryLine, VictoryScatter, VictoryAxis, VictoryTheme } from 'victory';

const data = [
  { x: 0, y: 0 },
  { x: 1, y: 0 },
  { x: 2, y: 0 },
  { x: 2, y: 1 },
  { x: 2, y: 2 },
  { x: 2, y: 3 },
  { x: 1, y: 3 },
  { x: 1, y: 2 },
  { x: 2, y: 2 },
  { x: 3, y: 2 },
  { x: 2, y: 2 }
];

const mapStateToProps = (state) => {
  return {
    inputInfo: state.inputData.input_data
  }
}

const strokeDasharray = "10, 5";

function PlotRoute (props) {
  const [coordinates, setCoordinates] = useState([]);

  useEffect(() => {
    console.log(props.inputInfo);

    fetch("/calculateroute", {
      method:"POST",
      headers:{'Content-Type': 'application/json'},
      body:JSON.stringify(props.inputInfo)
    })
    .then(response => response.json())
    .then(data => {setCoordinates(data);});
  }, [props.inputInfo]);

  return (
    <VictoryChart 
      height={200} 
      width={200} 
      padding={{ top: 30, bottom: 30, right: 30, left: 30 }}
      containerComponent={<VictoryVoronoiContainer/>}
    >
      <VictoryAxis crossAxis
        width={200}
        height={200}
        domain={[-10, 10]}
        theme={VictoryTheme.material}
        offsetY={100}
        standalone={false}
        style={{
          grid: {stroke: "#ECEFF1", strokeDasharray},
          ticks: {stroke: "grey", size: 1},
          tickLabels: {fontSize: 3, padding: 1}
        }}
      />
      <VictoryAxis dependentAxis crossAxis
        width={200}
        height={200}
        domain={[-10, 10]}
        theme={VictoryTheme.material}
        offsetX={100}
        standalone={false}
        style={{
          grid: {stroke: "#ECEFF1", strokeDasharray},
          ticks: {stroke: "grey", size: 1},
          tickLabels: {fontSize: 3, padding: 1}
        }}
      />
      <VictoryGroup
        color="#54B520"
        labels={({ datum }) => `x: ${datum.x} y: ${datum.y}`}
        labelComponent={
          <VictoryTooltip style={{ fontSize: 3 }} flyoutStyle={{ stroke: "tomato", strokeWidth: 1 }} flyoutHeight={10} flyoutWidth={15} pointerLength={5} pointerWidth={3}/>
        }
        data={coordinates}
      >
        <VictoryLine animate={{duration: 500, onLoad: { duration: 500 }}} style={{data: { strokeWidth: 1 }}} />
        <VictoryScatter size={({ active }) => active ? 2 : 1 } />
      </VictoryGroup>
    </VictoryChart>
  );
}


export default connect(mapStateToProps)(PlotRoute);
