import React, { Component } from 'react';

import { connect } from 'react-redux';
import { load_input_data } from '../Redux/inputData/inputData-action';


const mapDispatchToProps = dispatch => {
  return {
    load_input_data: (status) => dispatch(load_input_data(status))
  }
}

class ReadFile extends Component {
  constructor() {
    super();
  }

  showFile = (event) => {
    if (window.File && window.FileReader && window.FileList && window.Blob) {
      var preview = document.getElementById('show-text');
      var file = document.querySelector('input[type=file]').files[0];
      var reader = new FileReader();
      var textFile = /text.*/;
      var temp_data;

      if (file.type.match(textFile)) {
        reader.onload = event => {
          console.log("yoyoyo");
          temp_data = event.target.result;
          console.log("hihihi");
          console.log(temp_data);
          this.props.load_input_data(event.target.result);
        }
      } else {
        preview.innerHTML = "<span class='error'>It doesn't seem to be a text file!</span>";
      }
      reader.readAsText(file);
      this.setState({input_data: "ABC"});
    } else {
      alert("Your browser is too old to support HTML5 File API");
    }
  }

  render() {
    return (
      <div>
        <input type="file" onChange={this.showFile} />
        <div id="show-text">Choose text File</div>
      </div>
    );
  }
}


export default connect(null, mapDispatchToProps)(ReadFile);
